import React, { useState } from 'react'
import { 	StyleSheet, Text, View, TextInput, Dimensions, TouchableOpacity, TouchableWithoutFeedback, Keyboard } from 'react-native';
import { Icon } from 'react-native-elements';


export default function Login({navigation}) {
	const onPagar = () => {
		navigation.navigate("Payment");
	}

	const onLogin = () => {
		navigation.navigate("Login");
	}

	const onRegister = () => {
		navigation.navigate("Register");
	}

	const [email, guardarEmail] = useState('');
  const [password, guardarPassword] = useState('');
	const [mensaje, guardarMensaje] = useState(null);



	var iniciarSesion=()=>{
		const{username,password}=this.state;
		if ( username === ''||password==''){
			this.setState({mensajeError:'Ingrese usuario y clave!'})
		}else{
			var dataToSend = {username:username,password:password};
			var formBody = [];
			for (var key in dataToSend) {
				var encodedKey = encodeURIComponent(key);
            	var encodedValue = encodeURIComponent(dataToSend[key]);
            	formBody.push(encodedKey + "=" + encodedValue);
			}
			formBody = formBody.join("&");
			fetch('http://accountsolinal.pythonanywhere.com/api/login', {
            	method: "POST",//Request Type 
            	body: formBody,//post body 
            	headers: {//Header Defination 
                	'Content-Type': 'application/x-www-form-urlencoded;charset=UTF-8'
            	},
            })
            .then((response) => response.json())
